script:
frappe.ui.form.on('POS Invoice', 'test_send_sms', function (frm) {

    frappe.call({
        method: 'send-sms',
        args: {
            'currency': frm.doc.currency,
            'name': frm.doc.name,
            'posting_date': frm.doc.posting_date,
            'posting_time': frm.doc.posting_time,
            'contact_mobile': frm.doc.contact_mobile,
            'items': frm.doc.items,
            'customer_name': frm.doc.customer_name,
            'pos_profile': frm.doc.pos_profile,
            'total_qty': frm.doc.total_qty,
            'rounded_total': frm.doc.rounded_total,
            'status': frm.doc.status,
            'loyalty_program': frm.doc.loyalty_program,
            'redeem_loyalty_points': frm.doc.redeem_loyalty_points,
            'loyalty_points': frm.doc.loyalty_points,
            'customer': frm.doc.customer
        }
    });

});

frappe.ui.form.on('POS Invoice', {
    before_submit: function(frm) {
        if (!frm.doc.mpesa_receipt_number) {
            frappe.throw("Please ensure an M-Pesa Payment is done before checking out this POS Invoice.");
        }

        if (frm.doc.change_amount > 1) {
            frappe.throw("Change amount cannot be greater than 1");
        }
    }
});
